INSERT INTO `couch_fields` VALUES("100", "18", "internship_info", "Internship information", "", "richtext", "0", "text", "0", NULL, "\n				<h3>\n					Internships\n				</h3>\n\n				<h4>\n					Child & Family Therapists / Case Managers / Youth Life Coaches\n				</h4>\n\n				<p>\n\n					RAYS offers internship opportunities with a paid stipend for students who are\n					enrolled in accredited university programs. Both master\'s level and bachelor\'s\n					level students receive regular individual supervision. All internship\n					experiences will likely include providing services both agency-based and\n					within our school-based services program. Internships offered include the\n					following: Child and Family Therapist Intern (master\'s level), Case Manager\n					Intern (bachelor\'s level), Youth Life Coach (bachelor\'s level). All\n					experiences may include providing a combination of individual, family, and group\n					services.\n					\n				</p>\n\n				<p>\n					To apply please send a cover letter and resume to:\n				</p>\n				<p>\n					RAYS Attn. HR  web\n					<br>1025 South Third Street \n					<br>Renton, WA 98057 \n					<br>Fax: 425-227-8926\n				</p>\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'internship_info\'\r\nlabel=\'Internship information\'\r\ntype=\'richtext\'/>", "", NULL);
