INSERT INTO `couch_fields` VALUES("106", "10", "mental_health_story_2", "Mental Health story 2", "Warning: be extremely careful with any changes to keep formatting intact", "richtext", "0", "text", "6", NULL, "\n\n					<article id=\"story-2\" name=\"story-2\" class=\"story\">\n						\n						<h4>From one heart to another - <em>Julia, 2nd grade</em></h4>\n\n						<p>\n\n						Julia was referred to our counseling team by her mom\n						and teacher who were both concerned about her self-\n						esteem. Julia has ADHD and struggles with impulse\n						control, which can make it hard to learn, and hard to\n						make friends. She had fallen behind academically and\n						socially, and at her first meeting with her therapist\n						cried in her office asking: \"Why is it so much harder\n						for me to make a friend than everyone else?\"\n\n						</p>\n\n						<p class=\"quote-text\">\n\n							<blockquote class=\"quote-right\">On her own she\n						recognized her distress, gave her lots of\n						encouragement, and helped her problem solve.</blockquote>\n						\n						For six months Julia and her therapist worked on\n						friendship skills, identifying her strengths and\n						helping her learn to think positive thoughts about\n						herself. She is attending a summer social skills camp\n						at a Tukwila elementary school, and has quickly formed\n						a bond with another girl who has similar difficulties.\n						In fact on the first day of \"RAYS Camp\", Julia showed\n						remarkable empathy when her new friend became\n						frustrated during an art activity. On her own she\n						recognized her distress, gave her lots of\n						encouragement, and helped her problem solve. Her\n						therapist remarked that \"Julia has the biggest heart\n						I\'ve ever encountered in such a young person, and it\n						was a thrill to see her use some of the skills we\'ve\n						been practicing to help a friend. \"\n\n					</p>\n\n\n					</article>\n\n					", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'mental_health_story_2\'\r\nlabel=\'Mental Health story 2\'\r\ntype=\'richtext\'\r\norder=\'6\'\r\ndesc=\'Warning: be extremely careful with any changes to keep formatting intact\'/>", "", NULL);
INSERT INTO `couch_fields` VALUES("107", "10", "mental_health_overview", "Mental Health overview", "", "richtext", "0", "text", "3", NULL, "\n\n				<p>\n\n					For more than 40 years RAYS has worked with youth and\n					their families to overcome challenges, build healthier\n					habits, and more beneficial relationships. Through one-on-\n					one counseling, or communication with the whole family,\n					RAYS helps its clients build the capacity to improve their\n					own circumstances. We believe in a hollistic approach\n					considering the needs of all members of the family, while\n					never forgetting that it is the child-caregiver\n					relationship that is our primary client. We believe that\n					everyone can recover, if they are just have the tools to\n					do so.\n\n				</p>\n\n				<p>\n\n					At RAYS we celebrate both small and large victories alike,\n					because change can be difficult. RAYS serves primarily high-\n					needs families, and provides referrals to other services\n					and organizations when they are needed. By building skills that\n					help today, we believe that our communities will be stronger\n					tomorrow.\n\n				</p>\n\n\n				<h4>\n					Our expertise\n				</h4>\n\n				<p>\n\n					RAYS has extensive experience in working with people of\n					extremely diverse circumstances and cultural backgrounds.\n					We\'ve worked with all kinds of familial situations,\n					including foster families, grandparents raising\n					grandchildren, and homes of relative placements. Our staff\n					includes master\'s level trained therapists and bachelor\'s\n					level trained case managers with significant experience\n					with a broad range of issues including, but not limited\n					to:\n\n				</p>\n\n				<ul>\n					<li>Behavioral problems</li>\n					<li>Anger management</li>\n					<li>Domestic violence & child abuse</li>\n					<li>Child abuse prevention</li>\n					<li>Depression</li>\n					<li>Grief & loss</li>\n				</ul>\n\n\n				<h4>Our services</h4>\n\n				<p>\n\n					RAYS works closely with families and communities to build\n					on strengths and improve the emotional and behavioral\n					health of youth of all ages, in a wide variety of settings.\n\n				</p>\n\n				", "0", "0", "", "", "", "", "", "", "", "", "", "", "0", "0", "0", "", "0", "", "0", "0", "80", "0", "0", "0", "0", "0", "", "", "0", "<cms:editable\r\nname=\'mental_health_overview\'\r\nlabel=\'Mental Health overview\'\r\ntype=\'richtext\'\r\norder=\'3\'/>", "", NULL);
