INSERT INTO `wp_posts` VALUES("4", "1", "2013-08-23 20:31:34", "2013-08-23 20:31:34", "[new_royalslider id=\"1\"]\r\n\r\n<div class=\"block-text\">\r\n<h3 id=\"home-title\">Welcome to the pacific northwest</h3>\r\n<p>\r\nRetreat to the privacy of a modern cottage designed by Jetson Luddite Architects and featured in Coastal Living and Pacfic Northwest Magazines. Located on Point White, Bainbridge Islands best no-bank beach, the cottage is just steps away from breathtaking views of Mount Rainier, Rich Passage and Blake Island. The yard is professionally landscaped and the beach is accessed by a garden path.\r\n</p>\r\n<p>\r\nAmenities include a queen size bed and four twin bunks, a full bath, washer and dryer, fluffy terry robes and fresh linens.\r\n</p>\r\n</div>", "Home", "", "publish", "open", "open", "", "home", "", "", "2013-08-25 20:12:45", "2013-08-25 20:12:45", "", "0", "http://localhost/pw-house/wordpress/?page_id=4", "0", "page", "", "0");
