INSERT INTO `wp_posts` VALUES("21", "1", "2013-08-23 20:47:42", "2013-08-23 20:47:42", "<img alt=\"\" src=\"/pw-house/wordpress/wp-content/themes/pointwhite/img/pw-beach-wide.jpg\" />\r\n<div class=\"block-text\">\r\n<h2>A wealth of things to do</h2>\r\n<p>Hike the shore-to-forest trails of the neighborhood or jump on your mountain bike. Walk along the scenic beach or ride the double bike to shops, restaurants, entertainment and amenities including a gourmet bakery and deli, a luxurious day spa, live music at The Treehouse Caf, a neighborhood convenience shop, the 1912 Manor House and five-star Beach House Restaurant, the Pleasant Beach Village and the Historic Lynnwood Theatre, a vintage venue dating back to 1936. Enjoy the seasonal Sunday Market in the summer months, pick wild berries and apples in the fall, or settle in for winter stormwatching.</p>\r\n<h4>Activities</h4>\r\n<ul class=\"text-cols--2\">\r\n	<li>Fire Pit</li>\r\n	<li>Mountain Biking</li>\r\n	<li>Fort Ward State Park</li>\r\n	<li>Golf</li>\r\n	<li>Horseback Riding</li>\r\n	<li>Wildlife Viewing</li>\r\n	<li>Swimming</li>\r\n	<li>Equestrian Events</li>\r\n	<li>Sound/Bay Fishing</li>\r\n	<li>Sailing</li>\r\n	<li>Cycling</li>\r\n	<li>Hiking</li>\r\n	<li>Kayaking</li>\r\n	<li>Walking</li>\r\n	<li>Health/Beauty Spa</li>\r\n	<li>Winery Tours</li>\r\n	<li>Botanical Garden</li>\r\n	<li>Cinemas</li>\r\n	<li>Marina</li>\r\n	<li>Library</li>\r\n	<li>Live Theater</li>\r\n	<li>Beachcombing</li>\r\n	<li>Boating</li>\r\n	<li>Bird Watching</li>\r\n	<li>Gambling Casinos</li>\r\n	<li>Museums</li>\r\n	<li>Restaurants</li>\r\n	<li>Sight Seeing</li>\r\n	<li>Shopping</li>\r\n</ul>\r\n</div>", "Activities", "", "publish", "open", "closed", "", "activities", "", "", "2013-08-25 20:08:11", "2013-08-25 20:08:11", "", "0", "http://localhost/pw-house/wordpress/?page_id=21", "2", "page", "", "0");
