INSERT INTO `wp_posts` VALUES("54", "1", "2013-08-23 21:48:53", "2013-08-23 21:48:53", "\r\n\r\n<div class=\"block-text\">\r\n<h3>Welcome to the pacific northwest</h3>\r\nRetreat to the privacy of a modern cottage designed by Jetson Luddite Architects and featured in Coastal Living and Pacfic Northwest Magazines. Located on Point White, Bainbridge Islands best no-bank beach, the cottage is just steps away from breathtaking views of Mount Rainier, Rich Passage and Blake Island. The yard is professionally landscaped and the beach is accessed by a garden path.\r\n\r\nAmenities include a queen size bed and four twin bunks, a full bath, washer and dryer, fluffy terry robes and fresh linens. There is an abundance of materials for reading, writing, sketching, stargazing and dreaming. The cottage is set in a secluded garden and shares 180 feet of waterfront. Its a birders paradise, with kingfishers, redwing blackbirds, eagles, osprey, blue herons, pipers, swallows and songbirds. You might encounter otters, sea lions and even the occasional orca pod.\r\n\r\n</div>", "Home", "", "inherit", "open", "open", "", "4-autosave-v1", "", "", "2013-08-23 21:48:53", "2013-08-23 21:48:53", "", "4", "http://localhost/pw-house/wordpress/?p=54", "0", "revision", "", "0");
