INSERT INTO `wp_posts` VALUES("201", "1", "2013-08-25 20:13:34", "2013-08-25 20:13:34", "<img alt=\"\" src=\"/pw-house/wordpress/wp-content/themes/pointwhite/img/pw-ferry-wide.jpg\" />\r\n<div class=\"block-text\">\r\n<h2>Just the right distance away</h2>\r\n<p>\r\nThe Point White Guest House features an abundance of materials for reading, writing, sketching, stargazing and dreaming. The cottage is set in a secluded garden and shares 180 feet of waterfront. Its a birders paradise, with kingfishers, redwing blackbirds, eagles, osprey, blue herons, pipers, swallows and songbirds. You might encounter otters, sea lions and even the occasional orca pod.\r\n</p>\r\n<p>\r\nThe location is a short drive from downtown Bainbridge Island and the ferry dock. The island is a 35 minute ferry ride from downtown Seattle. There is bus and taxi service throughout the island.\r\n</p>\r\n<h4>Amenities</h4>\r\n<ul class=\"text-cols--2\">\r\n	<li>Cable TV</li>\r\n	<li>WiFi</li>\r\n	<li>Refrigerator</li>\r\n	<li>Microwave</li>\r\n	<li>Washer/dryer</li>\r\n	<li>Coffee service</li>\r\n	<li>Kettle and Toaster</li>\r\n	<li>Iron/ironing board</li>\r\n	<li>Tandem bike</li>\r\n	<li>Designated parking</li>\r\n</ul>\r\n</div>", "About", "", "inherit", "open", "open", "", "146-revision-v1", "", "", "2013-08-25 20:13:34", "2013-08-25 20:13:34", "", "146", "http://localhost/pw-house/wordpress/146-revision-v1/", "0", "revision", "", "0");
